package eeda.leads;import com.thoughtworks.selenium.Selenium;public class LeadsListPage {	private Selenium selenium;	public LeadsListPage(Selenium selenium) {		this.selenium = selenium;		System.out.println(selenium.getLocation());		if (!selenium.getLocation().equals("http://localhost:8080/list")) {			throw new IllegalStateException("This is not 房产资料列表 Page of logged in user, current page" + "is: " + selenium.getLocation());		}	}	public LeadsPage openNewLeads() {		selenium.open("/editLeads");		if (!selenium.getLocation().equals("http://localhost:8080/editLeads")) {			throw new IllegalStateException("This is not 新增房地产资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		return new LeadsPage(selenium);	}	public int getFirstLeadsID() {		String str = selenium.getTable("eeda-table.1.0");		System.out.println(str);		return 5;	}	public LeadsPage openEditLeads(int id) {		selenium.open("/editLeads/" + id);		if (!selenium.getLocation().startsWith("http://localhost:8080/editLeads/")) {			throw new IllegalStateException("This is not 编辑房地产资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		return new LeadsPage(selenium);	}}