package eeda.login;import com.thoughtworks.selenium.Selenium;public class LoginPage {	private Selenium selenium;	public LoginPage(Selenium selenium) {		this.selenium = selenium;		selenium.open("/");		if (!selenium.getTitle().equals("创诚易达信息共享平台")) {			throw new IllegalStateException("This is not login in page, current page is: " + selenium.getLocation());		}	}	/**	 * Login as demo user	 * 	 * @param userName	 * @param password	 * @return HomePage object	 */	public HomePage loginValidUser(String userName, String password) {		selenium.type("username", userName);		selenium.type("password", password);		selenium.click("xpath=//button[@type='submit']");		selenium.waitForPageToLoad("5000");		return new HomePage(selenium);	}}