package eeda.property;import com.thoughtworks.selenium.Selenium;public class ClientListPage {	private Selenium selenium;	public ClientListPage(Selenium selenium) {		this.selenium = selenium;		System.out.println(selenium.getLocation());		if (!selenium.getLocation().equals("http://localhost:8080/propertyClient")) {			throw new IllegalStateException("This is not 房产客户列表 Page of logged in user, current page" + "is: " + selenium.getLocation());		}	}	public ClientPage openNewClient() {		selenium.open("/propertyClient/edit");		if (!selenium.getLocation().equals("http://localhost:8080/propertyClient/edit")) {			throw new IllegalStateException("This is not 新增房产客户资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		return new ClientPage(selenium);	}	public ClientPage openEditClient() {		selenium.open("/propertyClient/edit/2");		if (!selenium.getLocation().equals("http://localhost:8080/propertyClient/edit/2")) {			throw new IllegalStateException("This is not 编辑房产客户资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		return new ClientPage(selenium);	}}