package eeda.property;import com.thoughtworks.selenium.Selenium;public class ClientPage {	private Selenium selenium;	public ClientPage(Selenium selenium) {		this.selenium = selenium;	}	public ClientPage inputNewClientAndSave() {		System.out.println(selenium.getLocation());		if (!selenium.getLocation().equals("http://localhost:8080/propertyClient/edit")) {			throw new IllegalStateException("This is not 新增房产客户资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		selenium.type("title", "unittest");		selenium.type("client_name", "unitest");		selenium.type("priority", "2重要不紧急");		selenium.type("statusSel", "求购");		selenium.type("type", "1房");		selenium.type("region", "老香洲");		selenium.type("area", "88");		selenium.type("total", "1200");		selenium.type("career", "IT工程师");		selenium.type("intro", "萨芬撒描述");		selenium.type("follow_info", "跟进情况");		selenium.type("introducer", "介绍人");		selenium.type("customer_source", "客户来源");		selenium.type("follower", "跟进人");		selenium.type("follower_phone", "跟进人电话");		selenium.type("client_phone1", "123456");		selenium.type("client_phone2", "1234567");		selenium.type("client_phone3", "0986657");		selenium.click("submit");		selenium.waitForPageToLoad("30000");		return new ClientPage(selenium);	}	public ClientPage inputClientAndSave() {		System.out.println(selenium.getLocation());		if (!selenium.getLocation().startsWith("http://localhost:8080/propertyClient/edit")) {			throw new IllegalStateException("This is not 编辑房产客户资料 Page of logged in user, current page" + "is: " + selenium.getLocation());		}		selenium.type("title", "unittest updated");		selenium.type("client_name", "unitest");		selenium.type("priority", "2重要不紧急");		selenium.type("statusSel", "求购");		selenium.type("type", "1房");		selenium.type("region", "老香洲");		selenium.type("area", "88");		selenium.type("total", "1200");		selenium.type("career", "IT工程师");		selenium.type("intro", "描述");		selenium.type("follow_info", "跟进情况");		selenium.type("introducer", "介绍人");		selenium.type("customer_source", "客户来源");		selenium.type("follower", "跟进人");		selenium.type("follower_phone", "跟进人电话");		selenium.type("client_phone1", "123456");		selenium.type("client_phone2", "1234567");		selenium.type("client_phone3", "0986657");		selenium.click("submit");		return new ClientPage(selenium);	}}