package eeda.login;import static org.junit.Assert.assertTrue;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebDriverBackedSelenium;import org.openqa.selenium.firefox.FirefoxDriver;import com.thoughtworks.selenium.Selenium;public class LoginPageTest {	WebDriver driver;	private Selenium selenium;	@Before	public void setUp() throws Exception {		driver = new FirefoxDriver();		selenium = new WebDriverBackedSelenium(driver, "http://localhost:8080");	}	@Test	public void testLoginDemo() {		LoginPage signInPage = new LoginPage(selenium);		HomePage homePage = signInPage.loginValidUser("demo", "123456");		assertTrue(selenium.getLocation().equals("http://localhost:8080/"));	}	@Test	public void testLoginManager() {		LoginPage signInPage = new LoginPage(selenium);		HomePage homePage = signInPage.loginValidUser("jason", "123456");		assertTrue(selenium.getLocation().equals("http://localhost:8080/"));	}	@After	public void tearDown() throws Exception {		selenium.stop();	}}